	private static final Pattern TODAY_PATTERN = Pattern.compile(".*?date%22%3A%22(.*?)%22%2C%22data.*?");

	private static final Pattern STEP_PATTERN = Pattern.compile(".*?ttl%5C%22%3A(.*?)%2C%5C%22dis.*?");

	private final ObjectMapper objectMapper = new ObjectMapper();

	private final String mobile;

	private final String password;

	private final Integer step;

	public XiaoMiSetStep(String mobile, String password, Integer step) {
		this.mobile = mobile;
		this.password = password;
		this.step = step;
	}


	public void run() {
		try {
			String accessToken = getAccessToken();
			UserInfo userInfo = login(accessToken);
			String appToken = getAppToken(userInfo.getLoginToken());
			setStep(userInfo, appToken);
		} catch (Exception e) {
			log.error("账号：{}，刷步失败", mobile, e);
		}

	}

	private String getAccessToken() {
		String url = "https://api-user.huami.com/registrations/+86" + mobile + "/tokens";
		Map<String, Object> param = new HashMap<>(8);
		param.put("client_id", "HuaMi");
		param.put("password", password);
		param.put("redirect_uri", "http://47.97.216.91:8081/access/" + mobile);
		param.put("token", "access");
		HttpResponse httpResponse = HttpRequest.post(url)
				.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)")
				.header("Content-Type", "application/x-www-form-urlencoded")
				.form(param)
				.timeout(20000)
				.execute();
		String location = httpResponse.header("Location");
		log.info("调用accessToken结果location：{}", location);
		String access = getFieldValue(location, "access");
		log.info("解析地址中access：{}", access);
		return access;
	}

	private UserInfo login(String access) throws JsonProcessingException {
		String url = "https://account.huami.com/v2/client/login";
		Map<String, Object> param = new HashMap<>();
		param.put("app_name", "com.xiaomi.hm.health");
		param.put("app_version", "4.6.0");
		param.put("code", access);
		param.put("country_code", "CN");
		param.put("device_id", "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1");
		param.put("device_model", "phone");
		param.put("grant_type", "access_token");
		param.put("third_name", "huami_phone");
		String result = HttpRequest.post(url)
				.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)")
				.header("Content-Type", "application/x-www-form-urlencoded")
				.form(param)
				.timeout(20000)
				.execute()
				.body();
		log.info("调用登录接口结果：{}", result);
		JsonNode jsonNode = objectMapper.readTree(result);
		String loginToken = jsonNode.get("token_info").get("login_token").asText();
		String userId = jsonNode.get("token_info").get("user_id").asText();
		return new UserInfo(loginToken, userId);
	}

	private String getAppToken(String loginToken) throws JsonProcessingException {
		String url =
				"https://account-cn.huami.com/v1/client/app_tokens?app_name=com.xiaomi.hm.health&dn=api-user.huami.com%2Capi-mifit.huami.com%2Capp-analytics.huami.com&login_token="
						+ loginToken;
		String result = HttpRequest.get(url)
				.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)")
				.header("Content-Type", "application/x-www-form-urlencoded")
				.execute()
				.body();
		log.info("调用获取appToken结果：{}", result);
		JsonNode jsonNode = objectMapper.readTree(result);
		return jsonNode.get("token_info").get("app_token").asText();
	}

	private void setStep(UserInfo userInfo, String appToken) {
		String userId = userInfo.getUserId();
		long now = System.currentTimeMillis();
		String today = DateUtil.format(new Date(), DatePattern.NORM_DATE_PATTERN);
		// 替换日期
		Matcher todayMatcher = TODAY_PATTERN.matcher(dataJson);
		if (todayMatcher.find()) {
			log.info("第一次替换：{}", todayMatcher.group(1));
			dataJson = dataJson.replace(todayMatcher.group(1), today);
		}
		// 替换步数
		Matcher stepMatcher = STEP_PATTERN.matcher(dataJson);
		if (stepMatcher.find()) {
			log.info("第二次替换：{}", stepMatcher.group(1));
			dataJson = dataJson.replace(stepMatcher.group(1), String.valueOf(step));
		}
		log.info("替换后dataJson：{}", dataJson);
		dataJson = URLDecoder.decode(this.dataJson, Charset.defaultCharset());

		String url = "https://api-mifit-cn.huami.com/v1/data/band_data.json?&t=" + now;
		Map<String, Object> param = new HashMap<>(8);
		param.put("userid", userId);
		param.put("last_sync_data_time", "1597306380");
		param.put("device_type", "0");
		param.put("device_id", "2C8B4939-0CCD-4E94-8CBA-CB8EA6E613A1");
		param.put("last_deviceid", "DA932FFFFE8816E7");
		param.put("data_json", this.dataJson);
		String result = HttpRequest.post(url)
				.header(Header.USER_AGENT, "MiFit/4.6.0 (iPhone; iOS 14.0.1; Scale/2.00)")
				.header("Content-Type", "application/x-www-form-urlencoded")
				.header("apptoken", appToken)
				.form(param)
				.timeout(20000)
				.execute()
				.body();
		log.info("账号：{}，刷步结果：{}", mobile, result);
	}

	private String getTime() throws JsonProcessingException {
		String url = "http://api.m.taobao.com/rest/api3.do?api=mtop.common.getTimestamp";
		String result = HttpRequest.post(url)
				.header(Header.USER_AGENT, "Dalvik/2.1.0 (Linux; U; Android 9; MI 6 MIUI/20.6.18)")
				.timeout(20000)
				.execute()
				.body();
		log.info("result:{}", result);
		JsonNode jsonNode = objectMapper.readTree(result);
		return jsonNode.get("data").get("t").asText();
	}

	public static String getParamByUrl(String url, String name) {
		url += "&";
		String pattern = "(\\?|&){1}#{0,1}" + name + "=[a-zA-Z0-9]*(&{1})";
		Pattern r = Pattern.compile(pattern);
		Matcher matcher = r.matcher(url);
		if (matcher.find()) {
			return matcher.group(0).split("=")[1].replace("&", "");
		} else {
			return "";
		}
	}

	private String getFieldValue(String urlStr, String field) {
		String result = "";
		Pattern pXM = Pattern.compile(field + "=([^&]*)");
		Matcher mXM = pXM.matcher(urlStr);
		while (mXM.find()) {
			result = mXM.group(1);
		}
		return result;
	}

